$(document).ready(()=>{$("#add-god-form").submit(b=>{b.preventDefault();console.log("creating new god");$.ajax({url:"/gods/create",method:"POST",data:{pantheon:$("#add-god-pantheon").val(),name:$("#add-god-name").val(),domain:$("#add-god-domain").val()},success:a=>{window.location="/storage";console.log(`god addition succeeded: status ${a.status}`)},error:a=>{$("#add-god-form").attr("class","ui error form");$("#add-god-error-message").text(a.message);console.error(`god addition failed: ${a.status} - ${a.message}`)}})})});
