const url=new URL(window.location);
$(document).ready(()=>{$.ajax({url:"/gods/get",method:"POST",data:{godId:url.searchParams.get("godId")},success:a=>{$("#edit-god-pantheon").val(a.pantheon);$("#edit-god-name").val(a.name);$("#edit-god-domain").val(a.domain)},error:a=>{$("#edit-god-form").attr("class","ui error form");$("#edit-god-error-message").text("Failed to get a god for this ID.")}});$("#edit-god-form").submit(a=>{a.preventDefault();$.ajax({url:"/gods/update",method:"POST",data:{godId:url.searchParams.get("godId"),pantheon:$("#edit-god-pantheon").val(),
name:$("#edit-god-name").val(),domain:$("#edit-god-domain").val()},success:()=>window.location="/storage",error:b=>{$("#edit-god-form").attr("class","ui error form");$("#edit-god-error-message").text(b.message)}})});$("#delete-god-button").click(()=>{$.ajax({url:"/gods/delete",method:"POST",data:{godId:url.searchParams.get("godId")},success:()=>window.location="/storage",error:a=>{$("#edit-god-form").attr("class","ui error form");$("#edit-god-error-message").text(a.message)}})})});
