$(document).ready(()=>{$("#login-form").submit(a=>{a.preventDefault();$.ajax({url:"/login",method:"POST",data:{username:$("#login-username").val(),password:$("#login-password").val()},success:()=>window.location="/storage",error:b=>{$("#login-form").attr("class","ui error form");$("#login-error-message").text(b.message)}})})});
